import React from 'react';
import { Search, Clock, Lock, Zap, Users, Layers } from 'lucide-react';
import { useState } from 'react';

const features = [
  { icon: Search, title: 'Smart search', description: 'AI-powered search to provide relevant results based on your needs.' },
  { icon: Clock, title: 'Lightning fast', description: 'Get results in milliseconds, not seconds. Speed is our priority.' },
  { icon: Lock, title: 'Secure by default', description: 'Your data is encrypted and protected with enterprise-grade security.' },
  { icon: Zap, title: 'Real-time sync', description: 'Always up-to-date results with real-time synchronization across all your apps.' },
  { icon: Users, title: 'Team collaboration', description: 'Share and collaborate on search results with your team members.' },
  { icon: Layers, title: 'Multi-platform', description: 'Works seamlessly across all your devices and operating systems.' },
];

export default function FeaturesSection() {
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);

  return (
    <section id="features" className="bg-gray-100 dark:bg-gray-800 py-20">
      <div className="mx-auto max-w-6xl px-4">
        <h2 className="mb-12 text-center text-4xl font-bold text-gray-900 dark:text-white">Powerful features for seamless searching</h2>
        <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
          {features.map((feature, index) => (
            <div
              key={index}
              className="group rounded-lg bg-white dark:bg-gray-700 p-6 shadow-lg transition duration-300 transform hover:scale-105 hover:shadow-xl"
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
            >
              <feature.icon className={`mb-4 h-12 w-12 ${hoveredIndex === index ? 'text-purple-600 dark:text-purple-400' : 'text-gray-600 dark:text-gray-300'} transition-colors duration-300`} />
              <h3 className="mb-2 text-xl font-semibold text-gray-900 dark:text-white">{feature.title}</h3>
              <p className="text-gray-600 dark:text-gray-300">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
