import React from 'react';
import { useState } from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react';
import type { FAQ } from '../types';

const faqs: FAQ[] = [
  {
    question: "How does the AI-powered search work?",
    answer: "Our AI-powered search uses advanced machine learning algorithms to understand the context of your query and provide the most relevant results across all your connected apps and documents."
  },
  {
    question: "Is my data secure?",
    answer: "Yes, we take data security very seriously. All your data is encrypted both in transit and at rest. We use industry-standard security protocols and regularly undergo security audits."
  },
  {
    question: "How many apps can I connect?",
    answer: "The number of apps you can connect depends on your plan. Our Basic plan allows up to 5 connected apps, while our Pro plan allows up to 20. For unlimited app connections, check out our Enterprise plan."
  },
  {
    question: "Can I try before I buy?",
    answer: "We offer a 14-day free trial on all our plans. You can test out all the features and see how our search solution can benefit your workflow before making a commitment."
  },
  {
    question: "How does billing work?",
    answer: "We offer monthly and annual billing options. Annual billing comes with a discount. You can upgrade, downgrade, or cancel your subscription at any time."
  }
];

export default function FAQSection() {
  const [openIndex, setOpenIndex] = useState<number | null>(null);

  return (
    <section id="faq" className="bg-white dark:bg-gray-900 py-20">
      <div className="mx-auto max-w-3xl px-4">
        <h2 className="mb-12 text-center text-4xl font-bold text-gray-900 dark:text-white">Frequently Asked Questions</h2>
        <div className="space-y-4">
          {faqs.map((faq, index) => (
            <div key={index} className="rounded-lg bg-gray-100 dark:bg-gray-800 transition duration-300 hover:shadow-md">
              <button
                className="flex w-full items-center justify-between p-4 text-left"
                onClick={() => setOpenIndex(openIndex === index ? null : index)}
              >
                <span className="text-lg font-semibold text-gray-900 dark:text-white">{faq.question}</span>
                {openIndex === index ? (
                  <ChevronUp className="h-5 w-5 text-purple-600 dark:text-purple-400" />
                ) : (
                  <ChevronDown className="h-5 w-5 text-purple-600 dark:text-purple-400" />
                )}
              </button>
              {openIndex === index && (
                <div className="p-4 pt-0 text-gray-600 dark:text-gray-300">
                  {faq.answer}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
